function hiToast(text,options){var defaultOptions={'thems':'Info','duration':3000,'fade-time':'normal','position':'bottom','container-id':'hitoast'};options=(typeof options=='object')?$.extend(defaultOptions,options):defaultOptions;var $container=$('#'+options['container-id']),thems_markup='';if(options.thems){thems_markup='toast-'+options.thems;}var html=$('<div class="toast  '+thems_markup+'">'+text+'</div>').fadeIn(options['fade-time']);$container.append(html);var width=($(window).width()/2-$(".toast").outerWidth()/2)+"px";var height='20px';if(options.position=='top'){height=($(window).height()*1/5-$(".toast").outerHeight()/2)+"px";}else if(options.position=='center'){height=($(window).height()/2-$(".toast").outerHeight()/2)+"px";}else if(options.position=='bottom'){height=($(window).height()*4/5-$(".toast").outerHeight()/2)+"px";}$(".toast").css("left",width);$(".toast").css("top",height);html.on('click',function(){hiToastX($(this));});setTimeout(function(){hiToastX(html);},options.duration);}function hiToastX(element,options){defaultOptions={'duration':'fast'};options=(typeof options=='object')?$.extend(defaultOptions,options):defaultOptions;if(typeof element!=="undefined"){element.fadeOut(options.duration,function(){$(this).remove();});}else{$('.toast').fadeOut(options.duration,function(){$(this).remove();});}}